package com.genomen.core;

import java.util.ArrayList;
import java.util.List;

/**
 * Presents a set of results generated by an analysis
 * @author ciszek
 */
public class Results {

    private String tag;
    private boolean intermediate;

    /**
     * Returns the tag name of this result set.
     * @return tag name
     */
    public String getTag() {
        return tag;
    }

    /**
     * Checks if this is an intermediate result set.
     * @return <code>true</code> if this is an intermediate result set, <code>false</code> otherwise
     */
    public boolean isIntermediate() {
        return intermediate;
    }

    /**
     * Constructs a result set with the given attributes.
     * @param p_tag the tag name of this result
     * @param isIntermediate is this an intermediate result
     */
    public Results( String p_tag, boolean isIntermediate ) {
        tag = p_tag;
        intermediate = isIntermediate;
    }

    /**
     * List of ResultEntities produced by the AnalyzationLogics.
     */
    private ArrayList<ResultEntity> resultList = new ArrayList<ResultEntity>();


    /**
     * Returns the list of results that compose this result set.
     * @return list of results
     */
    public List<ResultEntity> getResultEntities() {
        return resultList;
    }

    /**
     * Adds a result to this result set.
     * @param result <code>ResultEntity</code> to be added to this result set
     */
    public void addResult( ResultEntity result ) {

        resultList.add(result);

    }


    
}
